args:
/* --- MARKETING THEME: LUMINOUS --- */
.marketing-theme {
  /* Base: Clean / Bright */
  --background: oklch(0.99 0.005 150); /* White with very slight cool tint */
  --foreground: oklch(0.2 0.03 150); /* Dark green/grey text */

  /* Surfaces */
  --card: oklch(1 0 0); /* Pure white */
  --card-foreground: oklch(0.2 0.03 150);

  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.2 0.03 150);

  /* Primary: Emerald Green (Trust/Growth) */
  --primary: oklch(0.55 0.18 145); /* Solid Emerald */
  --primary-foreground: oklch(0.99 0.005 150); /* White text */

  /* Secondary: Soft Green */
  --secondary: oklch(0.96 0.02 145);
  --secondary-foreground: oklch(0.3 0.05 145);
  --secondary-content: var(--secondary-foreground);

  /* Muted */
  --muted: oklch(0.96 0.01 150);
  --muted-foreground: oklch(0.5 0.02 150);

  /* Accent */
  --accent: oklch(0.94 0.03 145);
  --accent-foreground: oklch(0.25 0.05 145);

  /* Destructive */
  --destructive: oklch(0.6 0.15 25);
  --destructive-foreground: oklch(0.98 0.01 150);

  /* Borders */
  --border: oklch(0.92 0.01 150);
  --input: oklch(0.92 0.01 150);
  --ring: oklch(0.55 0.18 145);
}
